contracts/access/RoleManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IDiamondCertificate.sol";

contract RoleManager is Ownable {
    mapping(bytes32 => mapping(address => bool)) public roles;

    bytes32 public constant MINER_ROLE = keccak256("MINER_ROLE");
    bytes32 public constant CUTTER_ROLE = keccak256("CUTTER_ROLE");
    bytes32 public constant CERTIFIER_ROLE = keccak256("CERTIFIER_ROLE");
    bytes32 public constant MANUFACTURER_ROLE = keccak256("MANUFACTURER_ROLE");

    constructor() Ownable() {
        _setupInitialRoles();
    }

    function _setupInitialRoles() private {
        address deployer = msg.sender;
        roles[MINER_ROLE][deployer] = true;
        roles[CUTTER_ROLE][deployer] = true;
        roles[CERTIFIER_ROLE][deployer] = true;
        roles[MANUFACTURER_ROLE][deployer] = true;
    }

    function grantRole(bytes32 role, address account) external onlyOwner {
        roles[role][account] = true;
    }

    function revokeRole(bytes32 role, address account) external onlyOwner {
        roles[role][account] = false;
    }

    function hasRole(bytes32 role, address account) public view returns (bool) {
        return roles[role][account];
    }

    function _canUpdateStatus(address account, IDiamondCertificate.CertificateStatus status) 
        internal 
        view 
        returns (bool) 
    {
        if (status == IDiamondCertificate.CertificateStatus.Mined) {
            return hasRole(MINER_ROLE, account);
        } else if (status == IDiamondCertificate.CertificateStatus.Cut) {
            return hasRole(CUTTER_ROLE, account);
        } else if (status == IDiamondCertificate.CertificateStatus.Certified) {
            return hasRole(CERTIFIER_ROLE, account);
        } else if (status == IDiamondCertificate.CertificateStatus.Manufactured) {
            return hasRole(MANUFACTURER_ROLE, account);
        }
        return false;
    }
}

contracts/DiamondCertificate.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IDiamondCertificate.sol";
import "./access/RoleManager.sol";
import "./storage/CertificateStorage.sol";

contract DiamondCertificate is IDiamondCertificate, RoleManager, CertificateStorage {
    constructor() RoleManager() {
        _nextCertificateId = 1;
    }

    function createCertificate(string memory ipfsHash) external override whenNotPaused returns (uint256) {
        require(hasRole(MANUFACTURER_ROLE, msg.sender), "Only manufacturer can create");
        
        uint256 newId = _nextCertificateId++;
        Certificate storage cert = _certificates[newId];
        cert.id = newId;
        cert.currentOwner = msg.sender;
        cert.ipfsHash = ipfsHash;
        cert.status = CertificateStatus.Created;
        cert.statusSignatures[uint(CertificateStatus.Created)] = msg.sender;
        cert.isTransferable = false;

        _addToHistory(newId, msg.sender, CertificateStatus.Created, ipfsHash);
        emit CertificateCreated(newId, ipfsHash);
        return newId;
    }

    function updateStatus(
        uint256 certificateId, 
        CertificateStatus newStatus, 
        string memory newIpfsHash
    ) external override whenNotPaused {
        Certificate storage cert = _certificates[certificateId];
        require(cert.id != 0, "Certificate does not exist");
        require(uint(newStatus) == uint(cert.status) + 1, "Invalid status transition");
        require(_canUpdateStatus(msg.sender, newStatus), "Unauthorized");
        
        cert.status = newStatus;
        cert.ipfsHash = newIpfsHash;
        cert.statusSignatures[uint(newStatus)] = msg.sender;
        
        _addToHistory(certificateId, msg.sender, newStatus, newIpfsHash);
        
        if (newStatus == CertificateStatus.Sold) {
            cert.isTransferable = true;
        }
        
        emit StatusUpdated(certificateId, newStatus);
    }

    function transferCertificate(uint256 certificateId, address to) external override whenNotPaused {
        Certificate storage cert = _certificates[certificateId];
        require(cert.currentOwner == msg.sender, "Not the owner");
        require(cert.isTransferable, "Certificate not transferable");
        require(to != address(0), "Invalid recipient");

        cert.currentOwner = to;
        emit CertificateTransferred(certificateId, msg.sender, to);
    }

    function getCertificateDetails(uint256 certificateId) 
        external 
        view 
        override
        returns (
            address currentOwner,
            string memory ipfsHash,
            CertificateStatus status,
            bool isTransferable
        ) 
    {
        Certificate storage cert = _certificates[certificateId];
        require(cert.id != 0, "Certificate does not exist");
        
        return (
            cert.currentOwner,
            cert.ipfsHash,
            cert.status,
            cert.isTransferable
        );
    }

    // 暂停和恢复功能
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function getCertificateHistory(uint256 certificateId) 
        external 
        view 
        override
        returns (
            uint256[] memory timestamps,
            address[] memory operators,
            CertificateStatus[] memory statuses,
            string[] memory ipfsHashes
        ) 
    {
        StatusHistory[] storage history = _certificateHistory[certificateId];
        uint256 length = history.length;
        
        timestamps = new uint256[](length);
        operators = new address[](length);
        statuses = new CertificateStatus[](length);
        ipfsHashes = new string[](length);
        
        for (uint256 i = 0; i < length; i++) {
            timestamps[i] = history[i].timestamp;
            operators[i] = history[i].operator;
            statuses[i] = history[i].status;
            ipfsHashes[i] = history[i].ipfsHash;
        }
        
        return (timestamps, operators, statuses, ipfsHashes);
    }
}

contracts/interfaces/IDiamondCertificate.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IDiamondCertificate {
    enum CertificateStatus {
        Created,
        Mined,
        Cut,
        Certified,
        Manufactured,
        Sold
    }

    event CertificateCreated(uint256 indexed id, string ipfsHash);
    event StatusUpdated(uint256 indexed id, CertificateStatus status);
    event CertificateTransferred(uint256 indexed id, address from, address to);
    event RoleGranted(bytes32 indexed role, address indexed account);
    event RoleRevoked(bytes32 indexed role, address indexed account);

    function createCertificate(string memory ipfsHash) external returns (uint256);
    function updateStatus(uint256 certificateId, CertificateStatus newStatus, string memory newIpfsHash) external;
    function transferCertificate(uint256 certificateId, address to) external;
    function getCertificateDetails(uint256 certificateId) external view returns (
        address currentOwner,
        string memory ipfsHash,
        CertificateStatus status,
        bool isTransferable
    );
    function getCertificateHistory(uint256 certificateId) external view returns (
        uint256[] memory timestamps,
        address[] memory operators,
        CertificateStatus[] memory statuses,
        string[] memory ipfsHashes
    );
}

contracts/storage/CertificateStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "../interfaces/IDiamondCertificate.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CertificateStorage is Pausable {
    struct Certificate {
        uint256 id;
        address currentOwner;
        string ipfsHash;
        IDiamondCertificate.CertificateStatus status;
        mapping(uint => address) statusSignatures;
        bool isTransferable;
    }

    struct StatusHistory {
        uint256 timestamp;
        address operator;
        IDiamondCertificate.CertificateStatus status;
        string ipfsHash;
    }

    mapping(uint256 => Certificate) internal _certificates;
    mapping(uint256 => StatusHistory[]) internal _certificateHistory;
    uint256 internal _nextCertificateId;

    function _addToHistory(
        uint256 certificateId,
        address operator,
        IDiamondCertificate.CertificateStatus status,
        string memory ipfsHash
    ) internal {
        _certificateHistory[certificateId].push(StatusHistory({
            timestamp: block.timestamp,
            operator: operator,
            status: status,
            ipfsHash: ipfsHash
        }));
    }
}
